{"filter":false,"title":"microposts_interface_test.rb","tooltip":"/sample_app/test/integration/microposts_interface_test.rb","undoManager":{"mark":0,"position":0,"stack":[[{"start":{"row":0,"column":0},"end":{"row":7,"column":0},"action":"remove","lines":["require 'test_helper'","","class MicropostsInterfaceTest < ActionDispatch::IntegrationTest","  # test \"the truth\" do","  #   assert true","  # end","end",""],"id":2},{"start":{"row":0,"column":0},"end":{"row":35,"column":3},"action":"insert","lines":["require 'test_helper'","","class MicropostsInterfaceTest < ActionDispatch::IntegrationTest","","  def setup","    @user = users(:michael)","  end","","  test \"micropost interface\" do","    log_in_as(@user)","    get root_path","    assert_select 'div.pagination'","    # 無効な送信","    assert_no_difference 'Micropost.count' do","      post microposts_path, params: { micropost: { content: \"\" } }","    end","    assert_select 'div#error_explanation'","    # 有効な送信","    content = \"This micropost really ties the room together\"","    assert_difference 'Micropost.count', 1 do","      post microposts_path, params: { micropost: { content: content } }","    end","    assert_redirected_to root_url","    follow_redirect!","    assert_match content, response.body","    # 投稿を削除する","    assert_select 'a', text: 'delete'","    first_micropost = @user.microposts.paginate(page: 1).first","    assert_difference 'Micropost.count', -1 do","      delete micropost_path(first_micropost)","    end","    # 違うユーザーのプロフィールにアクセス (削除リンクがないことを確認)","    get user_path(users(:archer))","    assert_select 'a', text: 'delete', count: 0","  end","end"]}]]},"ace":{"folds":[],"scrolltop":384,"scrollleft":0,"selection":{"start":{"row":35,"column":3},"end":{"row":35,"column":3},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":{"row":23,"state":"start","mode":"ace/mode/ruby"}},"timestamp":1575807091978,"hash":"87c9c4802008572825be363d9efaaa18c215